init-params.param:

                	try {
                		N_RX = N_HOPS1 = N_HOPS2 = N_HOPS3 = null;

                    	if (N_RX_HOPS != null) {
                    		N_RX = (short)(N_RX_HOPS & 0x0f);
                    		N_HOPS1 = (short)((N_RX_HOPS>>4) & 0x0f);
                    		N_HOPS2 = (short)((N_RX_HOPS>>8) & 0x0f);
                    		N_HOPS3 = (short)((N_RX_HOPS>>12) & 0x0f);
                    	}
                    	if (TEMP != null)
                    		TEMP = (double)TEMP / 100.0;
                    	if (VCC != null)
                    		VCC = ((double)VCC * 4.0) / 1023.0;
                    	if (CPU_DC != null)
                    		CPU_DC = (double)CPU_DC / 10.0;
                    	if (RF_DC != null)
                    		RF_DC = (double)RF_DC / 10.0;
					} catch(Throwable e) {
						org.apache.log4j.Logger.getLogger(gsn.processor.ScriptletProcessor.class).error(e);
					}
